  const handleChange = (event: { target: { name: string; value: string; }; }) => {
    if (event.target.name === 'filter') setFilter(event.target.value);
    if (event.target.name === 'search') setSearchText(event.target.value);
  };

  const handleSubmit = () => {
    let filteredBugs = bugsJson;
    if (searchText !== '') {
      filteredBugs = filteredBugs.filter((bug: any) => bug[filter].toLowerCase() === searchText.toLowerCase());
    }
    setBugs(filteredBugs);
    setSubmittedValue(`Filter Type: ${filter}, Search Text: ${searchText}`);
  };

  useEffect(() => {
    setBugs(bugsJson);
  }, []);

  return (
    <div style={{ width: '100vw', background: '#000', color: '#20C20E', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
      <div id='searchbar' style={{ display: 'flex', justifyContent: 'space-between', width: '50%' }}>
        <select name='filter' id='filter' onChange={handleChange}>
          <option value='language'>Language</option>
          <option value='owner'>Owner</option>
          <option value='severity'>Severity</option>
        </select>
        <input type='text' name='search' id='search' placeholder='Search...' onChange={handleChange} />
        <button type='button' onClick={handleSubmit}>Submit</button>
      </div>
      <div id='grid'>
        {bugs.map((bug: any, index: number) => (
          <div key={index}>ID: {bug.id}, Owner: {bug.owner}, Severity: {bug.severity}, Language: {bug.language}, Assigned to: {bug.assignedTo}</div>
        ))}
      </div>
      <div>{submittedValue}</div>
    </div>
  )
}

export default App
